SET SQL_LOG_BIN=0;
alter user 'root'@'localhost' identified by 'test';
GRANT ALL PRIVILEGES ON *.* TO 'root'@'%' IDENTIFIED BY 'test' WITH GRANT OPTION;
SET @@global.read_only=ON;
CREATE USER rpl_user@'%' IDENTIFIED BY 'test';
GRANT REPLICATION SLAVE ON *.* TO rpl_user@'%';
FLUSH PRIVILEGES;
SET SQL_LOG_BIN=1;
CHANGE MASTER TO MASTER_HOST='127.0.0.1', MASTER_PORT=5306, MASTER_USER='rpl_user', MASTER_PASSWORD='test', MASTER_AUTO_POSITION=1;
START SLAVE;